    function CRate(r) {
        $(""#Rating"").val(r);
        for (var i = 1; i <= r; i++) {
            $(""#Rate"" + i).attr('class', 'starGlow');
        }
        // unselect remaining
        for (var i = r + 1; i <= 10; i++) {
            $(""#Rate"" + i).attr('class', 'starFade');
        }
    }

    function CRateOver(r) {
        for (var i = 1; i <= r; i++) {
            $(""#Rate"" + i).attr('class', 'starGlow');
        }
    }

